--
-- Cấu trúc bảng cho bảng `product`
--

CREATE TABLE `product` (
  `id` int(11) NOT NULL,
  `name` varchar(45) COLLATE utf8mb4_unicode_ci NOT NULL,
  `price` int(11) NOT NULL,
  `sale` int(11) DEFAULT '0',
  `thumbnail` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `description` mediumtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `content` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `slug` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `cat_id` int(11) NOT NULL DEFAULT '0',
  `best_seller` int(11) DEFAULT '0',
  `status` int(11) DEFAULT '0',
  `created_at` datetime DEFAULT NULL,
  `updated_at` datetime DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `product`
--

INSERT INTO `product` (`id`, `name`, `price`, `sale`, `thumbnail`, `description`, `content`, `slug`, `cat_id`, `best_seller`, `status`, `created_at`, `updated_at`) VALUES
(1, 'Vở Học Sinh Tiểu Học', 20000, 0, 'img/pro11.jpg', 'Vở Học Sinh Tiểu Học - Ô Tô do Crabit Notebuck sản xuất. Là thương hiệu uy tín cung cấp sách và vở học sinh cho 28 tỉnh thành ở Việt Nam\r\n', 'Đây là các vật dụng thiết yếu cho học sinh tiểu học được thiết kế theo phong cách trẻ trung, sôi động.', 'vo-hoc-sinh-tieu-hoc', 9, 0, 1, '2019-02-21 17:18:46', '0000-00-00 00:00:00'),
(2, 'Bút bi Thiên Long', 5000, 0, 'img/pro1.jpg', 'Bút bi dành cho học sinh - sinh viên và văn phòng \r\nKiểu dáng hiện đại - phần tay cầm thiết kế vân giảm trơn trượt tạo sự thoải mái khi viết', 'Tính năng kỹ thuật:	Đầu bi 0.7mm mực nhập ngoại - nét viết trơn êm - mực ra đều. Độ dài viết tối đa 950m.\r\nMàu mực: Xanh - đỏ - đen\r\nĐóng gói: 48 cây/hộp - 1440 cây/thùng\r\nBảo quản & sử dụng: Đậy nắp sau khi sử dụng - Bảo quản ở nhiệt độ thường', 'but-bi-thien-long', 5, 0, 1, '2019-02-23 11:40:06', '0000-00-00 00:00:00'),
(3, 'Vở viết Envilo', 20000, 0, 'img/pro2.jpg', 'Vở được trang bị hệ thống đánh dấu bằng số thông minh cùng dòng kẻ in chính xác, rõ nét trên trang giấy, giúp việc học tập dễ dàng và thuận tiện hơn.', 'Bề mặt giấy trơn láng, viết đẹp, mượt mà\r\nKhổ giấy A5 thích hợp cho học sinh, sinh viên\r\nGáy vở đóng chắc chắn, không bong tróc', 'vo-viet-envilo', 10, 0, 1, '2019-02-23 11:40:06', '0000-00-00 00:00:00'),
(4, 'Vở tập viết', 30000, 0, 'img/pro4.jpg', 'Vở Tập Viết Chữ Hoa gồm các dạng bài tập rèn luyện lý thú, hữu ích.', 'Một trong những yêu cầu quan trọng để giúp trẻ học tốt chương trình lớp 1 là sự chuẩn bị đầy đủ các điều kiện về thể chất, trí tuệ, tình cảm, ngôn ngữ và một số kỹ năng cần thiết. Vì vậy, trẻ cần được dạy cách phát âm và làm quen với chữ cái, các mẫu chữ hoa - thường... Tủ Sách Bé Vào Lớp 1 là bộ sách hữu ích trong việc xây dựng nền tảng kiến thức, kĩ năng cho các em. Sách có nội dung phong phú với nhiều bài tập đa dạng theo hình thức trò chơi, phù hợp với các em trong độ tuổi 5 - 6 tuổi.\r\nMỗi tập nhấn mạnh vào một kỹ năng cơ bản: tô màu, tô chữ, tập phát âm, nối chữ và hình, tô nét cơ bản... Ngoài ra, sách còn kèm theo hình vẽ minh họa màu sinh động, giúp các em hứng thú hơn trong học tập.', 'vo-tap-viet', 13, 0, 1, '2019-02-23 11:40:06', '0000-00-00 00:00:00'),
(5, 'Vở ô li', 30000, 15, 'img/pro5.jpg', 'Cuốn sách là tài liệu bổ ích giúp các bé tự tin trong môn Chính tả lớp 1.', 'Để có nét chữ đẹp, ngoài những bài tập viết trong sách giáo khoa các em học sinh cần luyện tập thêm bài tập trong các sách bài tập.\r\nVở Ô Li Có Viết Chữ Mẫu Lớp 1 giới thiệu tới các em học sinh từ những nét chữ cơ bản như nét thẳng, nét móc, nét xiên..., những chữ đơn như, a, b, c... đến các từ ghép có kết hợp với thanh sắc như: bó, cô, lò cò... Sách còn có thêm những chữ ghép và từ ghép nâng cao như: kh - khế, ph - phố xá... giúp các em mở rộng được vốn từ ngữ cũng như rèn luyện được chữ viết.\r\nNgoài ra, hình thức vở kẻ ô li giúp các em rèn viết chữ sạch, đẹp, trình bày rõ ràng.', 'vo-o-li', 9, 0, 1, '2019-02-23 11:40:06', '0000-00-00 00:00:00'),
(6, 'Bút Máy Bizner', 50000, 0, 'img/pro6.jpg', 'Bút Máy Bizner mang vẻ đẹp Hiện Đại - Sang Trọng - Thể hiện đẳng cấp của người sử dụng.', 'Sản phẩm phù hợp cho Lãnh đạo, Giáo viên và Nhân viên văn phòng.', 'but-may-bizner', 6, 0, 1, '2019-02-23 11:40:06', '0000-00-00 00:00:00'),
(7, 'Bút Máy CEO', 60000, 0, 'img/pro7.jpg', 'Bút Máy CEO 774F phần thân trơn được làm từ kim loại cao cấp sơn tĩnh điện, kiểu dáng đẹp và sang trọng, nhỏ gọn.', 'Mực nhanh khô và không chất độc hại, rất an toàn cho người sử dụng, thân thiện với môi trường tự nhiên.\r\nKích cỡ bút thon gọn, mảnh mai tạo cảm giác thoải mái, vừa vặn, khi dùng lâu sẽ không làm đau hay tê cứng các ngón tay.\r\nKẹp bút thiết kế sang trọng, tên thương hiệu được in trên nắp bút, mang lại phong cách cho người sử dụng. Bút được thiết kế dạng đậy nắp tiện lợi giúp bạn có những nét bút đậm nét, đều và rõ ràng.\r\nSản phẩm rất thích hợp để làm quà tặng cho người thân và bạn bè, cũng là vật dụng thiết yếu trong cặp xách của các doanh nhân lẫn giới văn phòng.', 'but-may-ceo', 6, 0, 1, '2019-02-23 11:40:06', '0000-00-00 00:00:00'),
(8, 'Bút Máy Pilot Kakuno', 100000, 0, 'img/pro8.jpg', 'Bút Máy Pilot Kakuno - Xanh Dương được người dùng gọi là cây bút vui vẻ vì thiết kế hình mặt cười trên ngòi bút. Kakuno được thiết kế với tiêu chí là tạo cảm xúc thân thiện và trải nghiệm tốt nhất cho người cầm bút.', 'Ngòi bút bằng thép. Ngòi viết rất trơn, mượt không bị đứt nét hoặc khô.\r\nPhong cách trẻ trung, có 6 sắc màu tươi trẻ cho bạn lựa chọn.\r\nThân bút rất nhẹ giúp bạn viết lâu mà không gây mỏi tay\r\nThân và bút có hình lục giác với 3 tác dụng\r\nThiết kế giống với bút chì tạo cảm giác gần gũi với trẻ em khi mới bắt đầu\r\nDễ cầm nắm, chống tuột tay\r\nGiúp bút nằm yên trên bàn, khó lăn hoặc rơi xuống đất.\r\nCỡ nét : Có hai cỡ phổ biến thường dùng là Fine ( F) và Medium (M)', 'but-may-pilot-kakuno', 6, 0, 1, '2019-02-23 11:40:06', '0000-00-00 00:00:00'),
(9, 'Bút Bi Safari', 120000, 0, 'img/pro9.jpg', 'Bút bi cao cấp với phong cách thể hiện độc đáo và tối giản hóa. Phần tay cầm được thiết kế góc cạnh đặc biệt. Bên cạnh đó sự êm ái tuyệt vời ở ngòi bút cũng giúp cho sự lựa chọn sản phẩm này thêm hấp dẫn.', 'Chất liệu: Vỏ ngoài bằng Aluminium (Nhôm)\r\nMàu sắc: Màu Đỏ\r\nLoại ống mực: Lamy M 16, khi dùng hết có thể thay ống mực.', 'but-bi-safari', 5, 0, 1, '2019-02-23 11:40:06', '0000-00-00 00:00:00'),
(10, 'Bút Bi 10 Màu BT21', 30000, 0, 'img/pro10.jpg', 'Bút Bi 10 Màu BT21 cho bạn nét chữ thanh mảnh, sắc nét trên từng trang giấy.\r\n', 'Đầu bút nhỏ giúp cho bút không hao mực, bạn có thể thoải mái ghi chép, viết lách.\r\nMàu mực tươi tắn. Mực bút được làm từ nguyên liệu an toàn, thân thiện với môi trường.\r\nNét mực ra êm, nhanh khô, bền màu, không làm ướt, rách bề mặt giấy khi viết, mang đến sự tiện lợi và thoải mái tối đa khi sử dụng.\r\nKích cỡ bút thon gọn, tạo cảm giác thoải mái, vừa vặn, khi dùng lâu sẽ không làm đau hay tê cứng các ngón tay.\r\nSản phẩm phù hợp với nhân viên văn phòng, học sinh, sinh viên.', 'but-bi-10-mau', 5, 0, 1, '2019-02-23 11:40:06', '0000-00-00 00:00:00'),
(15, 'Vở Ô Ly Campus', 50000, 20, 'img/images/pro12.jpg', 'Vở Ô Ly Campus được làm từ chất liệu giấy ngoại nhập chất lượng cao, bề mặt giấy trơn láng, viết đẹp, mượt mà.\r\n', '<p>Định lượng 100g/m2, đạt ti&ecirc;u chuẩn giấy trắng ISO 82-84.</p>\r\n\r\n<p>Số trang 48, khổ 17 x 24 cm.</p>\r\n\r\n<p>Lốc gồm 10 quyển tập Campus.</p>\r\n\r\n<p>B&igrave;a vở trẻ trung, bắt mắt v&agrave; l&agrave; thiết kế độc quyền của Campus.</p>\r\n\r\n<p>G&aacute;y vở được đ&oacute;ng theo c&ocirc;ng nghệ &eacute;p keo đa lớp của Nhật Bản, gi&uacute;p vở lu&ocirc;n mở phẳng đẹp tr&ecirc;n b&agrave;n học, dễ d&agrave;ng lật v&agrave; viết từ trang đầu ti&ecirc;n đến trang cuối c&ugrave;ng.</p>\r\n\r\n<p>G&aacute;y vở vu&ocirc;ng đẹp, kh&ocirc;ng bị bong ra trong qu&aacute; tr&igrave;nh sử dụng.</p>\r\n\r\n<p>Vở Campus được trang bị hệ thống đ&aacute;nh dấu bằng số th&ocirc;ng minh c&ugrave;ng d&ograve;ng kẻ (4 &ocirc; ly 2.5 x 2.5 mm) in ch&iacute;nh x&aacute;c, r&otilde; n&eacute;t tr&ecirc;n trang giấy, gi&uacute;p việc học tập dễ d&agrave;ng v&agrave; thuận tiện hơn.</p>', 'vo-o-ly-campus', 9, 1, 1, '2019-04-12 03:09:12', '2019-05-01 17:57:43'),
(16, 'Bút Mực Gel Stabilo Palette', 50000, 0, 'img/pro13.jpg', 'Bút Mực Gel Stabilo Palette với màu mực đen thông dụng, dạng mực gel trơn bóng giúp viết chữ êm và đẹp hơn, thích hợp sử dụng cho trường học, văn phòng.', 'Bút có kích thước nhỏ gọn, đẹp mắt, có thể mang theo một cách dễ dàng. Đầu ngòi 0.5 mm cho nét viết rõ ràng và chuẩn xác, cho màu mực bóng và tươi hơn.\r\n\r\nThông tin sản phẩm\r\n- Loại sản phẩm: Bút mực gel.\r\n- Thương hiệu: Stabilo.\r\n- Xuất xứ: Malaysia.\r\n- Chất liệu: Mực gel cao cấp, màu sắc tươi sáng, an toàn cho sức khỏe người dùng.\r\n- Thiết kế:\r\n+ Bút có thiết kế đơn giản mà đẹp mắt, thân bút nhỏ gọn tiện lợi, vừa tay giúp người dùng viết chữ nhanh hơn.\r\n+ Màu mực gel cao cấp, bóng và tươi hơn mực bình thường, viết chữ êm và trôi đến giọt mực cuối cùng.\r\n+ Chất liệu mực an toàn cho sức khỏe người tiêu dùng.\r\n+ Ngòi bút có kích thước 0.5 mm cho nét chữ rõ ràng và chuẩn xác, mực ra đều, tránh tình trạng tắc nghẽn mực.\r\n- Màu mực: Màu đen.', 'but-muc-gel-stabilo-palette', 7, 0, 1, '2019-04-12 02:05:06', NULL),
(17, 'Bút Gel Doraemon ', 20000, 10, 'img/images/pro14.jpg', 'Bút Gel - Doraemon (Mực Tím) cho bạn nét chữ thanh mảnh, sắc nét trên từng trang giấy.', '<p>Đầu b&uacute;t nhỏ c&ograve;n gi&uacute;p cho b&uacute;t kh&ocirc;ng hao mực, bạn c&oacute; thể thoải m&aacute;i ghi ch&eacute;p, viết l&aacute;ch. Mực b&uacute;t được l&agrave;m tự nguy&ecirc;n liệu an to&agrave;n, th&acirc;n thiện với m&ocirc;i trường. C&ugrave;ng n&eacute;t mực ra &ecirc;m, nhanh kh&ocirc;, bền m&agrave;u, kh&ocirc;ng l&agrave;m ướt, r&aacute;ch bề mặt giấy khi viết, mang đến sự tiện lợi v&agrave; thoải m&aacute;i tối đa khi sử dụng. Với chất liệu cao cấp, m&agrave;u mực b&uacute;t ra đều, đẹp, kh&ocirc;ng phai m&agrave;u sau thời gian d&agrave;i sử dụng, rất th&iacute;ch hợp cho c&ocirc;ng việc văn ph&ograve;ng. Vỏ b&uacute;t nhiều m&agrave;u sắc tươi tắn, tạo n&ecirc;n điểm nổi bật cho sản phẩm. K&iacute;ch cỡ b&uacute;t thon gọn, tạo cảm gi&aacute;c thoải m&aacute;i, vừa vặn, khi d&ugrave;ng l&acirc;u sẽ kh&ocirc;ng l&agrave;m đau hay t&ecirc; cứng c&aacute;c ng&oacute;n tay.</p>', 'but-gel-doraemon', 7, 1, 1, '2019-04-12 03:03:09', '2019-05-01 17:58:26'),
(18, 'Vở Kẻ Ngang A4 Takeyo', 25000, 0, 'img/pro15.jpg', 'Vở Kẻ Ngang A4 Takeyo - Màu Ngẫu Nhiên có mặt giấy láng mịn, viết êm tay, tạo nét chữ đẹp, giấy viết không bị lem, ăn mực hầu hết các loại bút, giấy viết không nhòe, không thấm mực ra trang sau.\r\n', 'Quyển tập có đường kẻ ngang rõ ràng, đều đặn giúp các em học sinh viết chữ đẹp hơn, nắn nót hơn. Với 120 trang phù hợp cho các em học sinh cấp 1 sử dụng để viết bài hàng ngày.\r\nThiết kế gáy chắc chắn dễ dàng lật mở các trang khi viết mà không làm bung, hỏng giấy. Bìa vở trang trí hình ảnh họa tiết dễ thương, đơn giản, tạo cảm hứng cho các em trong quá trình học tập.\r\nSản phẩm sẽ là người bạn đồng hành giúp các bạn nhỏ học tốt hơn, góp phần đưa các em vươn tới sức mạnh của tri thức.', 'vo-ke-ngang-a4-takeyo', 10, 1, 1, '2019-04-12 03:03:09', NULL),
(19, 'Vở kẻ ngang BL-457', 30000, 0, 'img/pro16.jpg', 'Vở kẻ ngang BL-457 (64 trang) phù hợp với cả học sinh, sinh viên và nhân viên văn phòng. Giấy kẻ sẵn những hàng ngang, giúp bạn viết chữ ngay ngắn, gọn gàng và đẹp mắt.', 'Mặt giấy không gây lóa mắt\r\nSản phẩm sử dụng chất liệu giấy có độ trắng tự nhiên, chống lóa mắt, mỏi mắt khi đọc, viết. Đặc biệt chất liệu giấy mịn và dày giúp viết dễ dàng và không bị in mực ra trang sau. Ruột vở thiết kế khoa học, hợp lý, sử dụng tối đa diện tích.\r\nThiết kế gáy dễ dàng lật mở\r\nBìa vở in họa tiết cùng màu sắc nổi bật. Sản phẩm có thể sử dụng để ghi chép hay tô vẽ những ý tưởng, dữ liệu cá nhân một cách dễ dàng, nhanh chóng. Thiết kế gáy vở thông minh dễ dàng lật mở các trang mà không làm bung, hỏng giấy.', 'vo-ke-ngang-bl-457', 10, 1, 1, '2019-04-12 03:03:09', NULL),
(20, 'Bộ Dụng Cụ Học Tập Thiên Long', 125000, 0, 'img/pro17.jpg', 'Bộ Dụng Cụ Học Tập Thiên Long với rất nhiều dụng cụ khác nhau, rất cần thiết cho việc học tập của học sinh.\r\n', 'Bộ dụng cụ gồm có: 10 Ống Mực ( Màu Ngẫu Nhiên), 1 Gôm, 1 Bút Máy, 1 Thước kẻ, 1 hộp sáp màuCR-C028 18 màu\r\nBộ sản phẩm đều được làm từ chất liệu an toàn, thân thiện với môi trường, không gây hại đến sức khỏe người dùng.\r\nThiết kế nhỏ gọn và tiện lợi, thích hợp đựng trong bóp viết hoặc hộp bút một cách ngăn nắp, gọn gàng.', 'bo-dung-cu-hoc-tap-thien-long', 8, 1, 1, '2019-04-12 03:03:09', NULL),
(21, 'Bộ Dụng Cụ Học Sinh GGMM', 40000, 5, 'img/pro18.jpg', 'Bộ Dụng Cụ Học Sinh GGMM (Mẫu Ngẫu Nhiên) bao gồm 6 món dụng cụ thiết yếu cho các em học sinh bao gồm 1 bóp viết hoạt hình, 2 bút chì, 1 tẩy, 1 đồ chuốt, 1 thước kẻ. Đây sẽ là những món dụng cụ cần thiết cho bé trong quá trình học tập.\r\n', '<p>Bộ dụng cụ học sinh cho b&eacute; c&oacute; chất liệu tốt, an to&agrave;n, kh&ocirc;ng g&acirc;y độc hại cho b&eacute;, bởi tất cả đều l&agrave;m từ chất liệu an to&agrave;n cho sức khỏe của b&eacute; v&agrave; th&acirc;n thiện với m&ocirc;i trường. Chất liệu nhựa si&ecirc;u bền, chắc chắn v&agrave; đẹp mắt. Chất liệu chất lượng n&ecirc;n m&agrave;u sắc sản phẩm tươi s&aacute;ng như mới, kh&ocirc;ng phai m&agrave;u theo thời gian. Bộ dụng cụ với chủ đề rạp xiếc ngộ nghĩnh c&ugrave;ng m&agrave;u sắc sinh động tăng hứng th&uacute; học tập, ham hiểu biết cho trẻ. K&iacute;ch thước sản phẩm kh&ocirc;ng qu&aacute; lớn, gi&uacute;p b&eacute; dễ d&agrave;ng cất giữ cũng như mang theo khi cần di chuyển.</p>', 'bo-dung-cu-hoc-sinh-ggmm', 8, 1, 1, '2019-04-12 03:03:09', '2019-05-02 09:36:04'),
(22, 'Sáp Nặn Học Tập 12 Màu SMARTKIDS', 20000, 0, 'img/pro19.jpg', 'Sáp Nặn Học Tập 12 Màu SMARTKIDS có màu sắc tươi sáng, mềm dẻo, vừa dễ tạo hình, vừa không bị nứt khi sử dụng.\r\n', 'Sáp nặn có độ dầu cao, chống dính, vì vậy sau khi đắp các màu sắc khác nhau của sáp nặn để tạo hình, bé cũng rất dễ dàng để tách các màu ra.\r\nKhi để lâu trong không khí hay sử dụng trong thời gian dài, sáp nặn không bị khô cứng hay vón cục.\r\nVới loại sáp này, bé có thể trộn sáp nặn có nhiều màu khác nhau để tạo ra màu mới. Khi trộn sáp, màu được hình thành rất đẹp, đều mịn và tươi sáng.\r\nSáp màu an toàn, không độc hại, không tạo mùi khó chịu và không dính sáp ra tay bé khi sử dụng.', 'sap-nan-hoc-tap-12-mau-smartkids', 8, 0, 1, '2019-04-12 03:03:09', NULL),
(23, 'Bộ Dụng Cụ Eke Hộp Nhựa Chì Gỗ Deli ', 60000, 0, 'img/pro20.jpg', 'Bộ Dụng Cụ Eke Hộp Nhựa Chì Gỗ Deli (8 món) - Giao Màu Ngẫu Nhiên là các công cụ đơn giản nhưng không thể thiếu đối với học sinh, sinh viên trong quá trình học tập môn toán.\r\n', 'Bộ sản phẩm được đặt trong hộp nhựa gọn gàng và dễ dàng đem theo khi đi học.\r\nSản phẩm được làm từ chất liệu nhựa dẻo chắc chắn, có độ bền cao, an toàn cho người sử dụng và thân thiện với môi trường.\r\nThiết kế bề mặt thước có vạch chia kích thước theo đơn vị cm giúp bạn điều chỉnh và kẻ những đường thẳng, đường tròn chính xác nhất.\r\nTrên mặt thước, các đường kẻ góc và chia mức rõ ràng giúp bạn dễ sử dụng hơn.', 'bo-dung-cu-eke-hop-nhua-chi-go-deli', 8, 0, 1, '2019-04-12 03:03:09', NULL),
(24, 'Hộp Bút Hít ', 30000, 0, 'img/pro21.jpg', 'Hộp Bút Hít được làm từ chất liệu cao cấp, mềm mại, dễ dàng vệ sinh. Kích thước nhỏ gọn với hình dáng chữ nhật họa tiết đáng yêu.\r\n', 'Kiểu dáng độc đáo này, bạn có thể tùy ý sắp xếp vật dụng cá nhân của mình một cách gọn gàng, ngăn nắp và còn có thể mang theo bên người để sử dụng vào bất kỳ lúc nào.\r\nHộp bút sẽ giúp các bé yêu của bạn lưu trữ các dụng cụ học tập, bé có thể dễ dàng sử dụng hộp bút và sử dụng những dụng cụ mình cần. Đây cũng là cách giúp trẻ nhỏ biết cách sắp xếp ngăn nắp các dụng cụ khi còn nhỏ, tạo thói quen tốt khi lớn lên.', 'hop-but-hit', 8, 0, 1, '2019-04-12 03:03:09', NULL),
(25, 'Sổ Tay Notebook Minh Long', 40000, 0, 'img/pro22.jpg', 'Sổ Tay Notebook Minh Long - Spring Is Coming với thiết kế dễ thương và thông điệp ý nghĩa, đây sẽ là một trong những vật dụng cá nhân đem đến cho bạn niềm hứng khởi mỗi ngày.\r\n', 'Sản phẩm được làm từ chất liệu giấy tốt với độ chống thấm cao và bề mặt bám chữ hoàn hảo, thích hợp với nhiều loại viết, thuận tiện ghi chép trong công việc và học tập.\r\nSổ được thiết kế vô cùng dễ thương và đẹp mắt. Sự kết hợp hài hoà giữa màu sắc và hình ảnh đáng yêu sẽ đem đến cho chúng ta nhiều niềm vui mỗi ngày.\r\nPhần ruột giấy được thiết kế dưới cả hai hình thức: có dòng kẻ và không kẻ dòng, đáp ứng sở thích khác nhau của các bạn.\r\nVới 208 trang giấy (104 có dòng kẻ, 104 không có dòng kẻ) kích thước 13 x 18 cm, bạn có thể mang sổ theo bên mình khi đi học, làm việc, thuận tiện ghi chép mọi lúc mọi nơi.', 'so-tay-notebook-minh-long', 11, 0, 1, '2019-04-12 03:03:09', NULL),
(26, 'Sổ Tay ZNCI', 80000, 0, 'img/pro23.jpg', 'Sổ Tay ZNCI là vật dụng cần thiết giúp bạn ghi chú những thông tin quan trọng, ghi chú nội dung công việc quan trọng, kế hoạch hằng ngày, bài giảng, đồng thời còn là món quà ý nghĩa dành tặng cho người thân, bạn bè.\r\n', 'Thiết kế sổ rất nhỏ gọn, dễ dàng cho vào túi xách, ba lô để mang theo bên người khi ra ngoài.\r\nSản phẩm thích hợp cho nhiều đối tượng sử dụng khác nhau với màu sắc và thiết kế vô cùng đẹp mắt.', 'so-tay-znci', 11, 0, 1, '2019-04-12 03:03:09', NULL),
(27, 'Sổ Tay Ma Thuật A5 ', 30000, 0, 'img/pro24.jpg', 'Sổ Tay Ma Thuật A5 (Magic Rainbow Notebook) có thiết kế đẹp mắt với bìa màu và giấy trong màu đen, tạo cảm giác thích thú cho người sử dụng.\r\n', 'Số được làm từ chất liệu giấy C300, phủ bột than tự nhiên, không gây độc hại cho người sử dụng. Khi bạn viết hay vẽ vào sổ sẽ cho ra rất nhiều màu đẹp mắt (7 màu).\r\nSổ có kích thước khổ A5 cùng 20 trang cho bạn tha hồ ghi chép, vẽ vời. Gáy lò xo giúp bạn lật giở sổ một cách dễ dàng.\r\nCuốn sổ này sẽ rất thích hợp để bố mẹ tặng cho các em thiếu nhi bởi nó giúp kích thích sự sáng tạo, rèn luyện được sự tập trung cho bé. Màu sắc của từng hình vẽ, từng con chữ sẽ thay đổi một cách ngẫu nhiên và thu hút ánh mắt của con bạn hơn bất kỳ 1 trò chơi nào khác\r\nSổ Tay Ma Thuật A5 sẽ thêm những điều kỳ diệu mới chờ chúng ta cùng khám phá và thỏa sức sáng tạo.', 'so-tay-ma-thuat-a5', 11, 0, 1, '2019-04-12 03:03:09', NULL),
(28, 'Bé tập tô màu', 20000, 0, 'img/pro25.jpg', 'Bộ sách dành cho các đối tượng trẻ từ 2- 6 tuổi', 'Giúp tăng khả năng tư duy, nhận thức\r\nTập trung chú ý, các kỹ năng vận động tinh\r\nNgoài ra bộ sách còn rất phù hợp với các đối tượng trẻ chậm nói, chậm phát triển, trẻ tăng động, trẻ tự kỷ...', 'be-tap-to-mau', 12, 0, 1, '2019-04-12 03:03:09', NULL),
(29, 'Tập Tô Màu Dụng Cụ Học Tập', 10000, 0, 'img/pro26.jpg', 'Tô màu những bức tranh ngộ nghĩnh, vui tươi không những sẽ giúp bé có khoảng thời gian thật vui vẻ, mà còn giúp cho bé thỏa sức sáng tạo, phát triển năng khiếu nghệ thuật hội họa của mình.\r\n', 'Đồng hành cùng bộ sách tô màu thông qua những chủ điểm gần gũi như: Đồ vật gia đình, đồ ăn, trái cây, các loài hoa, phương tiện giao thông, động vật… các bé không những có được những giờ phút thư giãn thoải mái mà còn giúp bé rèn luyện tính kiên trì, óc quan sát, trí tưởng tượng phong phú qua từng nét tô vẽ trên mỗi bức tranh tạo bước tiến vững chắc đầu tiên cho bé trong cuộc sống.', 'tap-to-mau-dung-cu-hoc-tap', 12, 0, 1, '2019-04-12 03:03:09', NULL),
(30, 'Tập Tô Màu - Hãy Đợi Đấy', 10000, 0, 'img/pro27.jpg', 'Với những hình vẽ sinh động, hấp dẫn, các em hãy làm quen với thế giới màu sắc bằng cách tập tô màu theo những miếng decal mẫu có sẵn.', 'Kích thước: 20.5 x 28.5 cm\r\nNhà xuất bản:	Nhà Xuất Bản Văn Hoá Sài Gòn\r\nLoại bìa: Bìa mềm\r\nSố trang:	16', 'tap-to-mau-hay-doi-day', 12, 0, 1, '2019-04-12 03:03:09', NULL),
(31, 'Tập Tô Chữ', 12000, 0, 'img/pro28.jpg', 'Trước khi vào lớp 1, các bé cần phải được làm quen với các chữ viết cơ bản, để giúp bé không bỡ ngỡ khi mới tiếp xúc với môi trường tiểu học.', 'Bộ sách “Khởi đầu học viết” gồm 5 cuốn “Tập viết các nét cơ bản”, “Tập tô số”, “Tập tô vần”, “Tập tô chữ”, “Tập viết chữ cái” sẽ giới thiệu với các bé các nét chữ cơ bản, số đếm và bảng chữ cái tiếng Việt, đồng thời ở mỗi bài tập viết đều có các mẫu chữ với hướng dẫn cách viết từng nét cụ thể, giúp quý phụ huynh dễ dàng theo dõi và tập viết cho bé một cách hiệu quả.\r\n\r\nNgoài chữ viết, trong sách còn có những hình ảnh minh hoạ xinh xắn với màu sắc tươi sáng thu hút sự chú ý và sức tập trung của các bé.', 'tap-to-chu', 13, 0, 1, '2019-04-12 03:03:09', NULL),
(32, 'Bé Tập Viết Chữ Đẹp Tiếng Anh', 12000, 0, 'img/pro29.jpg', 'Quyển sách Bé Tập Viết Chữ Đẹp Tiếng Anh vừa giới thiệu cho bé một ngôn ngữ mới, vừa rèn chữ cho bé ngay trong những năm đầu, giúp bé có một nền tảng vững chắc về ngôn ngữ khi lên những bậc học cao hơn.', 'Hiện nay, tiếng Anh đã trở thành một ngôn ngữ quốc tế thông dụng và quan trọng trên mọi lĩnh vực. Vì vậy, việc học tiếng Anh là vô cùng thiết thực và cần thiết đối với tất cả mọi người nói chung và các em học sinh nói riêng. Quyển sách Bé Tập Viết Chữ Đẹp Tiếng Anh vừa giới thiệu cho bé một ngôn ngữ mới, vừa rèn chữ cho bé ngay trong những năm đầu, giúp bé có một nền tảng vững chắc về ngôn ngữ khi lên những bậc học cao hơn. Bên cạnh đó, tác giả cũng đưa ra các hình ảnh minh hoạ đẹp, ngộ nghĩnh, màu sắc phong phú nhằm tạo cho bé sự hứng thú khi tiếp nhận bài học.', 'be-tap-viet-chu-dep-tieng-anh', 13, 0, 1, '2019-04-12 03:03:09', NULL),
(33, 'MIMS Việt Nam', 500000, 0, 'img/pro30.jpg', 'MIMS Việt Nam xuất bản năm nay là nguồn thông tin cập nhật nhất của y, bác sỹ, dược sỹ về thông tin kê đơn đầy đủ và chi tiết của các dược phẩm lưu hành tại Việt Nam.\r\n', 'Ngoài các thông tin dược phẩm chi tiết, MIMS Việt Nam 2018 được bổ sung phần hình ảnh màu của các thuốc; các mục lục phân loại cũng được sắp xếp lại một cách khoa học giúp người đọc dễ dàng tra cứu thông tin. Thông tin trong MIMSViệt Nam là các tham khảo có giá trị giúp cho công việc điều trị và hướng dẫn bệnh nhân về cách dùng thuốc. Phần hìnhảnh sản phẩm giúp định dạng lại các thuốc bị mất nhãn hoặc các sản phẩm tương tự. MIMS Việt Nam  cung cấp một hệ thống thông tin dược phẩm đầy đủ. MIMS Việt Nam luôn luôn sẵn có tại bàn làm việc, bạn có thể tra cứu các thông tin chi tiết khi bạn cần kê đơn thông thường hoặc chuyên khoa.\r\nMIMS Việt Nam là một tài liệu tham khảo phong phú về thuốc nước ngoài và thuốc trong nước đang lưu hành tại Việt Nam, biên soạn công phu bố cục rõ ràng dễ tra cứu có phân tích các tác dụng phụ và tác dụng ngoài ý muốn của thuốc. MIMS Việt Nam 2018 sẽ là một tài liệu tham khảo có ích cho thầy thuốc', 'mims-viet-nam', 14, 0, 1, '2019-04-12 03:03:09', NULL),
(34, 'Cặp càng cua Office', 40000, 0, 'img/pro31.jpg', 'Cặp càng cua A4 được làm từ nguyên liệu nhựa PP cao cấp bọc bìa cứng\r\n', 'Có khóa còng lớn bằng thép mạ chắc chắn\r\nThuận tiện trong việc lưu trữ và bảo quản tài liệu lâu dài\r\nCó thể lưu giữ được tối đa 470 tờ giấy A4', 'cap-cang-cua-office', 15, 0, 1, '2019-04-12 03:03:09', NULL),
(35, 'Cặp hộp Hyphen', 25000, 0, 'img/pro32.png', 'Hộp đựng tài liệu Hyphen HP045 khổ A4 dày 5cm​ được sử dụng phổ biến trong môi trường văn phòng, công sở. Bìa hộp được làm từ carton cứng, có độ bền cao, chịu va đập tốt. Vải PVC bọc ngoài tạo cảm giác êm tay khi sử dụng.\r\n', 'Hộp đựng tài liệu có kích cỡ bằng khổ giấy A4, phù hợp với đa số các loại giấy tờ hiện nay, là sản phẩm lý tưởng để lưu giữ hồ sơ, giấy tờ quan trọng không bị nhăn, nhàu. Gáy hộp rộng 5cm, có thể đựng được tối đa 250 tờ. Đặc biệt, Hộp đựng tài liệu Hyphen HP045 khổ A4 dày 5cm​ được sản xuất trên dây chuyền công nghệ hiện đại, không chứa các hóa chất độc hại, an toàn cho sức khỏe người sử dụng.', 'cap-hop-hyphen', 16, 0, 1, '2019-04-12 03:03:09', NULL),
(36, 'Bìa Trình Ký Nẹp Giữa A4', 50000, 0, 'img/pro33.jpg', 'Bìa Trình Ký Nẹp Giữa A4 sở hữu kiểu dáng nhỏ gọn, màu sắc trang nhã giúp bạn thể hiện sự chuyên nghiệp trong lưu trữ giấy tờ, hồ sơ, đặc biệt trong các công việc ký kết, giao dịch với đối tác.\r\n', 'Đặc biệt, sản phẩm với thiết kế kẹp giữ giấy tiện lợi ở vị trí giữa bìa vừa giúp bạn dễ dàng cầm nắm sản phẩm chắc chắn, vừa giúp bạn cố định giấy tờ ngay ngắn, dễ tìm.\r\nĐồng thời, bìa với phần cắt tinh tế ở vị trí kẹp giúp giúp bạn quan sát, kiểm soát thông tin, giấy tờ thuận tiện hơn.\r\nNgoài ra, sản phẩm được làm từ chất liệu nhựa cứng cáp bền đẹp bảo quản tốt giấy tờ bên trong, tránh nhiều tác nhân như mối mọt, ẩm thấp…\r\nBìa trình ký là một trong những sản phẩm hữu ích, không thể thiếu đối với học sinh, sinh viên, công việc văn phòng để lưu giữ hồ sơ gọn gàng, có hệ thống.', 'bia-trinh-ky-nep-giua-a4', 17, 0, 1, '2019-04-12 03:03:09', NULL),
(37, 'Bìa trình ký đơn', 20000, 0, 'img/pro34.jpg', 'Bìa Trình Ký Đơn A4 là một trong những vật dụng hỗ trợ cần thiết trong môi trường văn phòng, công sở. ', 'Bìa có thiết kế khổ A4 tiện dụng, được làm bằng chất liệu nhựa chất lượng, cứng cáp, bền chắc, chịu được va đập tốt. Kẹp inox bền, không gỉ sét, có tính đàn hồi cao.', 'bia-trinh-ky-don', 17, 0, 1, '2019-04-12 03:03:09', NULL),
(38, 'Cặp Đựng Tài Liệu ', 1000000, 0, 'img/pro36.jpg', 'Cặp Đựng Tài Liệu - Xanh Navy được làm từ chất liệu da microfiber cao cấp siêu sạch, bền đẹp, nhẹ, đàn hồi, chống ẩm mốc, chống thấm nước tối ưu. Bên trong lót nỉ hoặc lót da.', 'Cặp có kiểu dáng sang trọng và hiện đại với sắc màu trang nhã, phù hợp với nơi công sở, thích hợp để bạn mang theo bên mình trong các buổi hội họp quan trọng.\r\nThiết kế tai cài bút, tai gài nam châm, nhiều ngăn nhỏ đựng thẻ ATM / namecard, ngăn chính và ngăn phụ tiện ích, đa năng: có thể chứa iPad / tablet, điện thoại thông minh, sổ note, tiền mặt, tài liệu tư trang cá nhân gọn nhẹ...', 'cap-dung-tai-lieu', 18, 0, 1, '2019-04-12 03:03:09', NULL),
(39, 'Túi zip đựng tài liệu', 100000, 0, 'img/pro35.jpg', 'Túi zip đựng tài liệu được làm từ nhựa có độ dẻo, dai, không sản sinh chất độc hại khi tiếp xúc với nhiều môi trường khác nhau như tủ lạnh, nước... an toàn cho sức khỏe người sử dụng. \r\n\r\n', 'Trải qua quá trình kiểm tra nghiêm ngặt, cẩn thận trước khi xuất ra thị trường, mỗi túi zip đều phải đáp ứng các tiêu chí về chất lượng cũng như tính thẩm mỹ.\r\nKhóa kéo dán kín miệng\r\nVới thiết kế thông minh, tiện dụng nhất là phần khóa kéo dán kín miệng, túi zip đựng đồ đa năng 40 x 50 cm​ được sử dụng nhiều trong việc bảo quản đồ dùng gia đình, thực phẩm, linh kiện điện tử, giày dép, quần áo... khỏi bụi bẩn, côn trùng xâm nhập. Bên cạnh đó, thiết kế khóa còn dễ dàng thao tác mà không gây tiếng ồn khó chịu. Túi màu trắng giúp bạn quan sát, phân biệt các đồ dụng, thiết bị bên trong một cách đơn giản.', 'tui-zip-dung-tai-lieu', 18, 0, 1, '2019-04-12 03:03:09', NULL),
(40, 'Balo Unisex', 500000, 0, 'img/pro37.jpg', 'Balo Unisex Thời Trang Đựng Laptop Chất Liệu Vải Canvas Si Mu', '', 'balo-unisex', 20, 0, 1, '2019-04-12 03:03:09', NULL),
(41, 'Túi Đeo Vai Đựng Máy Ảnh Chất Liệu Chống Thấm', 250000, 0, 'img/pro38.jpg', 'Túi Đeo Vai Đựng Máy Ảnh Chất Liệu Chống Thấm  Smart Earth P1200 - Đen được làm từ chất liệu cấp chống thấm tốt, các sợi được dệt tỉ mỉ, bảo vệ máy ảnh khỏi các va đập nhờ vào lớp đệm êm ái.\r\n', 'Kích thước gọn nhẹ nên cũng rất dễ dàng khi mang theo hay di chuyển ở bất cứ nơi đâu, khoá kéo zip chắc chắn.\r\nVới quai xách chịu lực, chống trơn trượt cùng quai đeo có thể điều chỉnh độ dài, với miếng đệm hạn chế mỏi vai, cân bằng lực tốt.\r\nThiết kế ba ngăn riêng biệt, tùy chọn sử dụng cho cả thân máy và ống kính.', 'tui-deo-vai-dung-may-anh-chat-lieu-chong-tham-smart-earth', 20, 0, 1, '2019-04-12 03:03:09', NULL),
(42, 'Túi Du Lịch Unisex', 700000, 0, 'img/pro39.jpg', 'Túi Du Lịch Unisex Chất Liệu Canvas giúp bạn đựng tất cả những quần áo vật dụng cần thiết để đi du lịch, tập gym, công tác hay cả khi đi làm với ngăn chứa lớn và nhiều ngăn nhỏ tiện lợi\r\n', 'Túi xách du lịch có độ bền cao với chất liệu Canvas không bao giờ lỗi mốt là một người bạn đồng hành không thể thiếu trong đời sống hàng ngày cũng như trong các chuyến du lịch đường dài.\r\nChất liệu vải canvas, bền màu, mau khô, không ám mùi. Lớp vải lót làm từ chất liệu polyester cao cấp chống mài mòn, chống phảy mưa, chống rách, khóa kéo chắc chắn.\r\nSức chứa lớn đến 55L, thiết kế lại thời trang, kích thước vừa vặn khiến túi trở thành sự lựa chọn tối ưu trong những chuyến du lịch ngắn ngày mà không cần phải bê nguyên một chiếc va-li cồng kềnh. \r\nDây đeo vai tuỳ chỉnh độ dài cùng quai xách tay để bạn tuỳ ý thay đổi kiểu cách và tiện lợi trong quá trình sử dụng.\r\nMàu sắc tươi tắn, trẻ trung dễ phối hợp với mọi loại trang phục như thời trang công sở hay áo thun, quần bò và giày sneaker năng động.', 'tui-du-lich-unisex', 20, 0, 1, '2019-04-12 03:03:09', NULL),
(43, 'Áo Bé Gái Đồng Phục', 180000, 0, 'img/pro40.jpg', 'Áo Bé Gái Đồng Phục Học Sinh Xếp Ply Túi Đắp Sanding - Trắng với thiết kế đơn giản nhưng không kém phần tinh tế.\r\n', 'Form áo phù hợp với mọi vóc dáng.\r\nNút gài phía trước chắc chắn, đường may tỉ mỉ.\r\nKiểu dáng thời trang với màu sắc trang nhã.\r\nSản phẩm phù hợp với quy định của hầu hết các trường học ở Việt Nam.', 'ao-be-gai-dong-phuc', 21, 0, 1, '2019-04-12 03:03:09', NULL),
(44, 'Quần short đồng phục học sinh', 120000, 0, 'img/pro41.jpg', 'Quần short đồng phục học sinh màu xanh đen Sanding có thiết kế khỏe khoắn, mang lại nét năng động và cảm giác thoải mái cho các bé mỗi khi tới trường.\r\n', '- Chất liệu 100% polyester, giúp người mặc có cảm giác dễ chịu, không bị gò bó.\r\n- Thiết kế dáng quần ống suông, phù hợp với nhiều form dáng khác nhau.\r\n- Đường chỉ may chắc chắn, khóa quần trơn, dễ kéo, không gây tiếng động lớn khi kéo.\r\n- Kết hợp với áo sơ mi trắng để bộ đồng phục đến trường thêm phù hợp với phong cách thanh lịch.', 'quan-short-dong-phuc-hoc-sinh', 21, 0, 1, '2019-04-12 03:03:09', NULL),
(45, 'Mẫu Áo Đồng Phục Xiaomi', 150000, 0, 'img/pro42.jpg', ' Chất vải tốt - Giá cả hợp lý\r\n', '* Bộ sản phẩm gồm : 1 Áo\r\n* Logo in phun cao cấp thấm màu lên vải \r\n* Họa tiết hoa văn in phun kỹ thuật số sắc nét, không bong tróc.\r\n* In tên, in số, in logo, IN NHANH-BỀN - ĐẸP, hình in bảo hành vĩnh viễn', 'mau-ao-dong-phuc-xiaomi', 21, 0, 1, '2019-04-12 03:03:09', NULL),
(46, 'Bàn kèm giá sách', 1400000, 0, 'img/pro43.jpg', 'Bàn laptop đa năng  là mẫu bàn nhiều khổ  đơn giản, tiện lợi, phù hợp sử dụng mọi nơi trong không gian nhà. Sản phẩm có khung thép sơn tĩnh điện chắc chắn, mặt bàn được làm bằng gỗ công nghiệp phủ melanine chống xước, chống nước bền đẹp. Một sản phẩm bàn laptop di động hiện đại cho cuộc sống năng động.', 'Xuất xứ : Đài Loan\r\n\r\nKích thước : 120cm(dài) x 600cm(rộng) x 100cm(cao)\r\n\r\nChức năng của Ban : làm việc, giải trí, bàn laptop, bàn để đồ ăn trong không gian nhỏ, có nhiều chức năng tiết kiệm không gian sinh hoạt và giải trí đa dạng.\r\n\r\nMàu sắc lựa chọn : Trắng, đen, nâu, vàng …\r\n\r\nMặt bàn rộng sử dụng gỗ ép cao cấp phủ melamine chống bám bụi chống xước.\r\n\r\nVới thiết kế đa dụng, bạn có thể sử dụng vào nhiều mục đích như làm việc, học tập, sử dụng laptop hay ăn trên bàn. Kích thước nhỏ của bàn giúp bạn có thể đặt ở những vị trí nhỏ hẹp mà không sợ tốn diện tích đồng thời có thể nâng, hạ độ cao phù hợp cho mọi lứa tuổi.', 'ban-kem-gia-sach', 22, 0, 1, '2019-04-12 03:03:09', NULL),
(47, 'Bàn học thông minh', 6000000, 0, 'img/pro44.jpg', 'Bàn học thông minh D801 được thiết kế bởi các chuyên gia Đức, với thiết kế tiện dụng tiết kiệm không gian.', 'Bàn học được thiết kế tiện dụng, dễ dàng thay đổi chiều cao phù hợp với chiều cao của trẻ.\r\nMặt bàn có thể điều chỉnh nghiêng từ 0 - 60 độ giúp bé dễ dàng đọc, viết, vẽ..\r\nBề mặt bàn được phủ lớp laminate chống lóa bảo vệ mắt của trẻ.\r\nDễ dàng thay đổi chiều cao phù hợp cho trẻ từ mẫu giáo đến đại học.\r\nMặt bàn có thể điều chỉnh độ nghiêng giúp trẻ viết, đọc vẽ dễ dàng.\r\nBàn thiết kế từ những vật liệu đạt chuẩn chất lượng Châu Âu, thân thiện với người dùng.', 'ban-hoc-thong-minh', 22, 0, 1, '2019-04-12 03:03:09', NULL),
(48, 'Bảng từ viết bút lông', 380000, 0, 'img/pro45.jpg', 'Bảng từ viết bút lông TH01 trắng với nhiều kích thước, chứa được nhiều nội dung viết cùng lúc, sẽ là công cụ hỗ trợ đắc lực cho công việc giảng dạy tại nhà trường. ', 'Đặc biệt, bảng cho phép dùng nam châm đính hình ảnh minh họa, hoặc đính giấy note lên bảng rất tiện dụng. Bảng được thiết kế với 4 cạnh được bọc khung nhôm chắc chắn, có tác dụng chống oxy hóa, đảm bảo tính chịu lực cao. Ngoài ra, 4 góc của bảng còn có co nhựa màu đen , hài hòa với khung nhôm, chống trầy xước và an toàn cho người sử dụng.', 'bang-tu-viet-but-long', 22, 0, 1, '2019-04-12 03:03:09', NULL),
(49, 'Bảng Viết Phấn', 140000, 0, 'img/pro46.jpg', 'Được làm từ các nguyên liệu cao cấp, nhập khẩu từ Hàn Quốc, không bị cong vênh, chống ẩm mốc, mang lại giá trị sử dụng lâu bền. 4 góc của bảng được co nhựa màu trắng hoặc đen, hài hòa với khung nhôm, chống trầy xước, nên rất an toàn cho người dùng. Với Bảng TH01 sẽ là công cụ học tập bổ ích cho trẻ, giúp cho việc học của trẻ được liên tục không bị gián đoạn, không mất thời gian lau sạch bảng nhiều lần.', 'Thông tin sản phẩm\r\n\r\nLoại bút viết thích hợp\r\n\r\nBảng từ TH01 thích hợp sử dụng viết phấn, dùng trong công tác giảng dạy ở nhà trường, dùng làm dụng cụ hỗ trợ học tập cho học sinh tại nhà, hoặc sử dụng cho bất kì nhu cầu viết nào của bạn.\r\n\r\nChất liệu cao cấp\r\n\r\n- Bảng được làm từ chất liệu cao cấp, có mặt bảng nhập khẩu từ Hàn Quốc. Mặt bảng màu xanh đậm không bị lóa khi sử dụng,  viết chữ rõ ràng không bị lem phấn, dễ dàng lau sạch không để lại dấu.\r\n\r\n- Bảng từ được lót bằng ván MDF có tính chịu lực tốt, độ bền tối ưu, giúp bảng không bị cong vênh, không bị rung khi viết. Mặt sau của bảng được ép giấy bóng vân gỗ chống ẩm, mang lại giá trị sử dụng lâu bền cho sản phẩm.\r\n\r\nThiết kế tiện dụng, an toàn\r\n\r\n- 4 cạnh của  được bọc khung nhôm hộp trắng, bóng, mang lại tính thẩm mỹ cao. Khung nhôm được thiết kế chắc chắn, có tác dụng chống oxy hóa, đảm bảo tính chịu lực cao. Ngoài ra, 4 góc của bảng còn có co nhựa màu trắng hoặc đen, hài hòa với khung nhôm, chống trầy xước và an toàn cho người sử dụng.\r\n\r\nThông tin khác \r\n\r\n- Nguyên liệu được nhập khẩu từ Hàn Quốc', 'bang-viet-phan', 22, 0, 1, '2019-04-12 03:03:09', NULL),
(50, 'Kệ sách để bàn', 300000, 0, 'img/pro47.jpg', 'Kệ sách để bàn', '<p>K&iacute;ch thước: 35x35x22,2cm</p>\r\n\r\n<p>M&agrave;u sắc: N&acirc;u/Đen/Kem</p>\r\n\r\n<p>M&atilde;: B05</p>\r\n\r\n<p>K&iacute;ch thước: 48 x 23 x 28cm</p>\r\n\r\n<p>M&agrave;u sắc: N&acirc;u/Đen/Kem</p>\r\n\r\n<p>M&atilde;: B04</p>\r\n\r\n<p>K&iacute;ch thước: 48 x 23 x 28cm</p>\r\n\r\n<p>M&agrave;u sắc: N&acirc;u/Đen/Kem</p>', 'ke-sach-de-ban', 22, 0, 1, '2019-04-12 03:03:09', '0000-00-00 00:00:00'),
(51, 'Kệ Sách 6 Tầng Ghép', 300000, 0, 'img/pro48.jpg', 'Chất liệu gỗ cao cấp, độ bền cao\r\nThiết kế bắt mắt, sang trọng, hiện đại\r\nMàu sắc tinh tế, hài hoà với mọi không gian\r\nSản phẩm dễ vệ sinh lau chùi trong quá trình sử dụng', 'Kệ Sách 6 Tầng Ghép (Kệ Tài Liệu) nhỏ gọn, tiện ích thích hợp đặt ở phòng khách hoặc phòng làm việc. Bạn cũng có thể để vật dụng trang trí kèm các loại sách và tạp chí nhằm tăng tính thẩm mỹ cho sản phẩm.\r\nKệ sách được làm từ gỗ công nghiệp bền bỉ, chịu lực tốt.\r\nBên ngoài được phủ lớp sơn có khả năng chống ẩm mốc, mối mọt. Bề mặt trơn nhẵn đảm bảo an toàn cho người dùng và dễ dàng lau chùi khi bị bám bẩn.\r\nNhững tấm gỗ được tháo rời và đóng gói cẩn thận, khi sử dụng chỉ cần lắp ráp như hướng dẫn kèm theo sản phẩm.\r\nKệ dễ dàng tháo rời nên bạn có thể lắp ráp thành hai kệ 3 tầng và 2 tầng hay kệ 5 tầng tùy theo sở thích cá nhân.\r\nThiết kế màu sắc trang nhã, sang trọng, thích hợp với mọi không gian.', 'ke-sach-6-tang-ghep', 22, 0, 1, '2019-04-12 03:03:09', NULL),
(52, 'Kệ Sách Slagelse', 3500000, 0, 'img/pro49.jpg', 'Màu sắc trang nhã\r\nDễ lắp đặt\r\nSử dụng lâu dài', 'Kệ Sách Slagelse được sản xuất từ chất liệu HDF, ván ép, nhựa đã trải qua quá trình xử lý chống mối mọt, cong vênh.\r\n\r\nSản phẩm bền đẹp, chắc chắn, cho thời gian sử dụng lâu dài và đảm bảo tính thẩm mỹ.\r\n\r\nKệ gỗ giúp không gian trong nhà trở nên gần gũi với thiên nhiên hơn, mang lại cho người sử dụng cảm giác thoải mái.', 'ke-sach-slagelse', 22, 0, 1, '2019-04-12 03:03:09', NULL),
(53, 'Giấy Photo Idea A4', 75000, 0, 'img/pro50.jpg', 'Giấy có độ cản quang lớn\r\nCó độ trắng cao\r\nBề mặt giấy khá dày và láng mịn', 'Giấy Photo Idea A4 DL70 với kích thước A4, thân thiện với môi trường và thích hợp với hầu hết các loại máy in phun, máy in laser, máy fax laser, máy photocopy.\r\nSản phẩm thiết kế khổ giấy A4, thích hợp sử dụng làm giấy in, photo trong các văn phòng hoặc trong gia đình. Bạn cũng có thể sử dụng để viết, vẽ sơ đồ hoặc phác thảo các bản vẽ một cách đơn giản và nhanh chóng.\r\nGiấy có bề dày tốt, bề mặt láng mịn, độ cản quang của giấy cao hơn do đó giảm hiện tượng nhìn thấu trang và cho phép sử dụng cả hai mặt giấy một cách toàn diện nhất.\r\nChất liệu giấy an toàn, không chứa chất gây độc hại và mùi khó chịu, thân thiện với môi trường và giúp bảo vệ sức khỏe người dùng, đặc biệt là mắt. Giấy mịn, bắt mực êm.', 'giay-photo-idea-a4', 2, 0, 1, '2019-04-12 03:03:09', NULL);

